using System.ComponentModel.Composition;
using Microsoft.Dynamics.ApplicationPlatform.FeatureExposure;

/// <summary>
/// The <c>FTBETrialBalanceFilterFeature</c> class defines the feature, when enabled, will add additional filters to the trial balance form
/// </summary>
[ExportAttribute(identifierStr(Microsoft.Dynamics.ApplicationPlatform.FeatureExposure.IFeatureMetadata))]
[Microsoft.Dynamics.BusinessPlatform.SharedTypes.InternalUseOnlyAttribute]
internal final class FTBETrialBalanceFilterFeature implements IFeatureMetadata
{
    static FTBETrialBalanceFilterFeature instance = new FTBETrialBalanceFilterFeature();

}

Method.Source:
/// <summary>
/// Gets the trial balance filter feature instance
/// </summary>
/// <returns>The trial balance filter feature instance</returns>
[Hookable(false)]
public static FTBETrialBalanceFilterFeature instance()
{
    return FTBETrialBalanceFilterFeature::instance;
}

/// <summary>
/// Determines whether the feature is enabled.
/// </summary>
/// <returns>
/// Returns true if the feature in Feature Management is enabled; otherwise, false.
/// </returns>
[Hookable(false)]
public static boolean isEnabled()
{
    return Dynamics.AX.Application.FeatureStateProvider::isFeatureEnabled(FTBETrialBalanceFilterFeature::instance());
}
